// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./interfaces/IGuarantorRegistry.sol";
import "./interfaces/IReputationSystem.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract GuarantorRegistry is IGuarantorRegistry, ReentrancyGuard, Ownable {
    uint256 public constant INITIAL_REPUTATION = 100;
    uint256 public constant MIN_REPUTATION_PRIMARY = 200;
    uint256 public constant MIN_REPUTATION_SECONDARY = 100;
    uint256 public constant REPUTATION_SUCCESS_BONUS = 10;
    uint256 public constant REPUTATION_FAILURE_PENALTY = 50;
    uint256 public constant REPUTATION_COLLUSION_PENALTY = 100;
    uint256 public constant WHISTLEBLOWER_BONUS = 20;
    uint256 public constant BAN_DURATION_FAILURE = 30 days;
    uint256 public constant BAN_DURATION_COLLUSION = 365 days;

    uint256 public constant COMMITMENT_WINDOW = 2 days;
    uint256 public constant REVEAL_WINDOW = 1 days;

    uint256 public constant MIN_STAKE_PERCENTAGE = 10;
    uint256 public constant MAX_STAKE_PERCENTAGE = 30;

    mapping(address => GuarantorProfile) private guarantorProfiles;
    mapping(uint256 => mapping(address => GuarantorCommitment)) private escrowCommitments;
    mapping(uint256 => address[]) private escrowGuarantorsList;
    mapping(uint256 => mapping(bytes32 => bool)) private usedSecrets;
    mapping(uint256 => uint256) private escrowCommitmentDeadlines;

    address public athenyxCore;

    modifier onlyCore() {
        if (msg.sender != athenyxCore) revert Unauthorized();
        _;
    }

    modifier notBanned(address guarantor) {
        GuarantorProfile storage profile = guarantorProfiles[guarantor];
        if (profile.isBanned && block.timestamp < profile.bannedUntil) {
            revert GuarantorIsBanned();
        }
        _;
    }

    error Unauthorized();
    error AlreadyRegistered();
    error InvalidStakeAmount();
    error InvalidTier();
    error InvalidAddress();

    constructor() Ownable(msg.sender) {}

    function setAthenyxCore(address coreAddress) external onlyOwner {
        if (coreAddress == address(0)) revert InvalidAddress();
        athenyxCore = coreAddress;
    }

    function registerGuarantor() external {
        GuarantorProfile storage profile = guarantorProfiles[msg.sender];

        if (profile.registeredAt != 0) revert AlreadyRegistered();

        profile.guarantorAddress = msg.sender;
        profile.reputationScore = INITIAL_REPUTATION;
        profile.totalStaked = 0;
        profile.availableStake = 0;
        profile.successfulGuarantees = 0;
        profile.failedGuarantees = 0;
        profile.activeGuarantees = 0;
        profile.isBanned = false;
        profile.bannedUntil = 0;
        profile.registeredAt = block.timestamp;

        emit GuarantorRegistered(msg.sender, block.timestamp);
    }

    function commitAsGuarantor(
        uint256 escrowId,
        GuarantorTier tier,
        uint256 stakeAmount,
        bytes32 commitmentHash
    ) external notBanned(msg.sender) {
        GuarantorProfile storage profile = guarantorProfiles[msg.sender];

        if (profile.registeredAt == 0) revert GuarantorNotRegistered();
        if (escrowCommitments[escrowId][msg.sender].commitmentTimestamp != 0) {
            revert CommitmentAlreadyExists();
        }
        if (stakeAmount == 0) revert InvalidStakeAmount();
        if (tier == GuarantorTier.NONE) revert InvalidTier();

        if (tier == GuarantorTier.PRIMARY && profile.reputationScore < MIN_REPUTATION_PRIMARY) {
            revert InsufficientReputation(MIN_REPUTATION_PRIMARY, profile.reputationScore);
        }
        if (tier == GuarantorTier.SECONDARY && profile.reputationScore < MIN_REPUTATION_SECONDARY) {
            revert InsufficientReputation(MIN_REPUTATION_SECONDARY, profile.reputationScore);
        }

        GuarantorCommitment storage commitment = escrowCommitments[escrowId][msg.sender];
        commitment.guarantorAddress = msg.sender;
        commitment.escrowId = escrowId;
        commitment.tier = tier;
        commitment.stakedAmount = stakeAmount;
        commitment.commitmentHash = commitmentHash;
        commitment.commitmentTimestamp = block.timestamp;
        commitment.isRevealed = false;
        commitment.isActive = true;

        escrowGuarantorsList[escrowId].push(msg.sender);
        profile.activeGuarantees++;
        profile.totalStaked += stakeAmount;

        if (escrowCommitmentDeadlines[escrowId] == 0) {
            escrowCommitmentDeadlines[escrowId] = block.timestamp + COMMITMENT_WINDOW;
        }

        emit GuarantorCommitted(msg.sender, escrowId, tier, stakeAmount);
    }

    function revealCommitment(
        uint256 escrowId,
        bytes32 secret
    ) external {
        GuarantorCommitment storage commitment = escrowCommitments[escrowId][msg.sender];

        if (commitment.commitmentTimestamp == 0) revert CommitmentNotFound();
        if (commitment.isRevealed) revert CommitmentAlreadyExists();

        uint256 commitDeadline = escrowCommitmentDeadlines[escrowId];
        if (block.timestamp < commitDeadline || block.timestamp > commitDeadline + REVEAL_WINDOW) {
            revert RevealWindowClosed();
        }

        bytes32 expectedHash = keccak256(abi.encodePacked(msg.sender, secret, escrowId));
        if (expectedHash != commitment.commitmentHash) revert InvalidSecret();

        if (usedSecrets[escrowId][secret]) revert CollusionDetected();

        usedSecrets[escrowId][secret] = true;
        commitment.isRevealed = true;

        emit GuarantorRevealed(msg.sender, escrowId, secret);
    }

    function slashGuarantor(
        address guarantor,
        uint256 escrowId,
        uint256 amount
    ) external onlyCore nonReentrant {
        GuarantorProfile storage profile = guarantorProfiles[guarantor];
        GuarantorCommitment storage commitment = escrowCommitments[escrowId][guarantor];

        if (commitment.stakedAmount < amount) {
            amount = commitment.stakedAmount;
        }

        commitment.stakedAmount -= amount;
        profile.totalStaked -= amount;
        profile.failedGuarantees++;
        profile.activeGuarantees--;

        emit GuarantorSlashed(guarantor, escrowId, amount);
    }

    function rewardGuarantor(
        address guarantor,
        uint256 escrowId,
        uint256 amount
    ) external onlyCore nonReentrant {
        GuarantorProfile storage profile = guarantorProfiles[guarantor];
        GuarantorCommitment storage commitment = escrowCommitments[escrowId][guarantor];

        profile.successfulGuarantees++;
        profile.activeGuarantees--;

        uint256 totalReturn = commitment.stakedAmount + amount;
        commitment.stakedAmount = 0;
        profile.totalStaked -= commitment.stakedAmount;

        (bool success, ) = guarantor.call{value: totalReturn}("");
        if (!success) revert TransferFailed();

        emit GuarantorRewarded(guarantor, escrowId, amount);
    }

    function updateReputation(
        address guarantor,
        int256 change
    ) external onlyCore {
        GuarantorProfile storage profile = guarantorProfiles[guarantor];

        if (change > 0) {
            profile.reputationScore += uint256(change);
        } else {
            uint256 decrease = uint256(-change);
            if (profile.reputationScore > decrease) {
                profile.reputationScore -= decrease;
            } else {
                profile.reputationScore = 0;
            }
        }

        emit ReputationUpdated(guarantor, change, profile.reputationScore);
    }

    function banGuarantor(
        address guarantor,
        uint256 duration,
        string calldata reason
    ) external onlyCore {
        GuarantorProfile storage profile = guarantorProfiles[guarantor];

        profile.isBanned = true;
        profile.bannedUntil = block.timestamp + duration;

        emit GuarantorBannedEvent(guarantor, profile.bannedUntil, reason);
    }

    function getGuarantorProfile(address guarantor) external view returns (GuarantorProfile memory) {
        return guarantorProfiles[guarantor];
    }

    function getCommitment(
        uint256 escrowId,
        address guarantor
    ) external view returns (GuarantorCommitment memory) {
        return escrowCommitments[escrowId][guarantor];
    }

    function isEligibleGuarantor(
        address guarantor,
        GuarantorTier tier,
        uint256 requiredStake
    ) external view returns (bool) {
        GuarantorProfile storage profile = guarantorProfiles[guarantor];

        if (profile.registeredAt == 0) return false;
        if (profile.isBanned && block.timestamp < profile.bannedUntil) return false;
        if (profile.availableStake < requiredStake) return false;

        if (tier == GuarantorTier.PRIMARY) {
            return profile.reputationScore >= MIN_REPUTATION_PRIMARY;
        } else if (tier == GuarantorTier.SECONDARY) {
            return profile.reputationScore >= MIN_REPUTATION_SECONDARY;
        }

        return false;
    }

    function getEscrowGuarantors(uint256 escrowId) external view returns (address[] memory) {
        return escrowGuarantorsList[escrowId];
    }

    error TransferFailed();

    receive() external payable {}
}

contracts/InsurancePool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./interfaces/IInsurancePool.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract InsurancePool is IInsurancePool, ReentrancyGuard, Ownable {
    uint256 public constant PREMIUM_RATE_BASE = 1000;
    uint256 public constant PREMIUM_PERCENTAGE = 10;
    uint256 public constant MIN_RESERVE_RATIO = 20;
    uint256 public constant FULL_COLLATERAL_PAYOUT = 100;
    uint256 public constant PARTIAL_COLLATERAL_PAYOUT = 50;

    struct Claim {
        uint256 escrowId;
        address claimant;
        uint256 requestedAmount;
        uint256 approvedAmount;
        bool processed;
        bool approved;
        uint256 filedAt;
        bytes evidence;
    }

    PoolStats private poolStats;

    mapping(uint256 => uint256) private escrowPremiums;
    mapping(uint256 => Claim) private claims;
    mapping(uint256 => bool) private claimExists;

    address public athenyxCore;
    address public treasuryAddress;

    modifier onlyCore() {
        if (msg.sender != athenyxCore) revert Unauthorized();
        _;
    }

    error Unauthorized();
    error InvalidTreasuryAddress();

    constructor(address treasury) Ownable(msg.sender) {
        if (treasury == address(0)) revert InvalidTreasuryAddress();
        treasuryAddress = treasury;

        poolStats.totalCollected = 0;
        poolStats.totalPaidOut = 0;
        poolStats.currentBalance = 0;
        poolStats.reserveRatio = 100;
        poolStats.activeClaims = 0;
    }

    function setAthenyxCore(address coreAddress) external onlyOwner {
        if (coreAddress == address(0)) revert Unauthorized();
        athenyxCore = coreAddress;
    }

    function setTreasuryAddress(address treasury) external onlyOwner {
        if (treasury == address(0)) revert InvalidTreasuryAddress();
        treasuryAddress = treasury;
    }

    function collectPremium(uint256 escrowId) external payable onlyCore {
        if (msg.value == 0) revert InvalidClaimAmount();

        escrowPremiums[escrowId] += msg.value;
        poolStats.totalCollected += msg.value;
        poolStats.currentBalance += msg.value;

        _updateReserveRatio();

        emit PremiumCollected(escrowId, msg.value, msg.sender);
    }

    function fileClaim(
        uint256 escrowId,
        uint256 requestedAmount,
        bytes calldata evidence
    ) external {
        if (claimExists[escrowId]) revert ClaimAlreadyExists();
        if (requestedAmount == 0) revert InvalidClaimAmount();
        if (requestedAmount > poolStats.currentBalance) {
            revert InsufficientPoolBalance(requestedAmount, poolStats.currentBalance);
        }

        claims[escrowId] = Claim({
            escrowId: escrowId,
            claimant: msg.sender,
            requestedAmount: requestedAmount,
            approvedAmount: 0,
            processed: false,
            approved: false,
            filedAt: block.timestamp,
            evidence: evidence
        });

        claimExists[escrowId] = true;
        poolStats.activeClaims++;

        emit ClaimFiled(escrowId, msg.sender, requestedAmount);
    }

    function processClaim(
        uint256 escrowId,
        bool approved,
        uint256 payoutAmount
    ) external onlyCore nonReentrant {
        if (!claimExists[escrowId]) revert ClaimNotFound();

        Claim storage claim = claims[escrowId];

        if (claim.processed) revert ClaimAlreadyExists();

        claim.processed = true;
        claim.approved = approved;

        if (approved) {
            if (payoutAmount > poolStats.currentBalance) {
                revert InsufficientPoolBalance(payoutAmount, poolStats.currentBalance);
            }

            claim.approvedAmount = payoutAmount;
            poolStats.currentBalance -= payoutAmount;
            poolStats.totalPaidOut += payoutAmount;
            poolStats.activeClaims--;

            (bool success, ) = claim.claimant.call{value: payoutAmount}("");
            if (!success) revert TransferFailed();

            _updateReserveRatio();

            emit ClaimApproved(escrowId, claim.claimant, payoutAmount);
            emit PayoutProcessed(escrowId, claim.claimant, payoutAmount);
        } else {
            poolStats.activeClaims--;
            emit ClaimRejected(escrowId, claim.claimant, "Claim rejected by core");
        }
    }

    function getPoolStats() external view returns (PoolStats memory) {
        return poolStats;
    }

    function calculatePremium(
        uint256 loanAmount,
        uint256 duration,
        uint256 riskScore
    ) external pure returns (uint256) {
        uint256 basePremium = (loanAmount * PREMIUM_PERCENTAGE) / 100;

        uint256 durationMultiplier = duration / 30 days;
        if (durationMultiplier == 0) durationMultiplier = 1;

        uint256 riskMultiplier = 100 + riskScore;

        uint256 totalPremium = (basePremium * durationMultiplier * riskMultiplier) / (100 * PREMIUM_RATE_BASE);

        return totalPremium;
    }

    function getAvailableLiquidity() external view returns (uint256) {
        return poolStats.currentBalance;
    }

    function getClaim(uint256 escrowId) external view returns (Claim memory) {
        return claims[escrowId];
    }

    function getEscrowPremium(uint256 escrowId) external view returns (uint256) {
        return escrowPremiums[escrowId];
    }

    function withdrawExcessReserves() external onlyOwner nonReentrant {
        uint256 requiredReserve = (poolStats.totalCollected * MIN_RESERVE_RATIO) / 100;

        if (poolStats.currentBalance <= requiredReserve) revert InsufficientPoolBalance(0, 0);

        uint256 excessAmount = poolStats.currentBalance - requiredReserve;

        poolStats.currentBalance -= excessAmount;

        (bool success, ) = treasuryAddress.call{value: excessAmount}("");
        if (!success) revert TransferFailed();
    }

    function _updateReserveRatio() private {
        if (poolStats.totalCollected == 0) {
            poolStats.reserveRatio = 100;
        } else {
            poolStats.reserveRatio = (poolStats.currentBalance * 100) / poolStats.totalCollected;
        }
    }

    error TransferFailed();

    receive() external payable {
        poolStats.totalCollected += msg.value;
        poolStats.currentBalance += msg.value;
        _updateReserveRatio();
    }
}

contracts/interfaces/IGuarantorRegistry..content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

interface IGuarantorRegistry {
    enum GuarantorTier {
        NONE,
        SECONDARY,
        PRIMARY
    }

    struct GuarantorProfile {
        address guarantorAddress;
        uint256 reputationScore;
        uint256 totalStaked;
        uint256 availableStake;
        uint256 successfulGuarantees;
        uint256 failedGuarantees;
        uint256 activeGuarantees;
        bool isBanned;
        uint256 bannedUntil;
        uint256 registeredAt;
    }

    struct GuarantorCommitment {
        address guarantorAddress;
        uint256 escrowId;
        GuarantorTier tier;
        uint256 stakedAmount;
        bytes32 commitmentHash;
        uint256 commitmentTimestamp;
        bool isRevealed;
        bool isActive;
    }

    event GuarantorRegistered(address indexed guarantor, uint256 timestamp);
    event GuarantorCommitted(address indexed guarantor, uint256 indexed escrowId, GuarantorTier tier, uint256 stake);
    event GuarantorRevealed(address indexed guarantor, uint256 indexed escrowId, bytes32 secret);
    event GuarantorSlashed(address indexed guarantor, uint256 indexed escrowId, uint256 slashedAmount);
    event GuarantorRewarded(address indexed guarantor, uint256 indexed escrowId, uint256 rewardAmount);
    event ReputationUpdated(address indexed guarantor, int256 change, uint256 newScore);
    event GuarantorBannedEvent(address indexed guarantor, uint256 bannedUntil, string reason);

    error GuarantorNotRegistered();
    error GuarantorIsBanned();
    error InsufficientReputation(uint256 required, uint256 current);
    error InsufficientStake(uint256 required, uint256 available);
    error CommitmentAlreadyExists();
    error CommitmentNotFound();
    error RevealWindowClosed();
    error InvalidSecret();
    error CollusionDetected();

    function registerGuarantor() external;

    function commitAsGuarantor(
        uint256 escrowId,
        GuarantorTier tier,
        uint256 stakeAmount,
        bytes32 commitmentHash
    ) external;

    function revealCommitment(
        uint256 escrowId,
        bytes32 secret
    ) external;

    function slashGuarantor(
        address guarantor,
        uint256 escrowId,
        uint256 amount
    ) external;

    function rewardGuarantor(
        address guarantor,
        uint256 escrowId,
        uint256 amount
    ) external;

    function updateReputation(
        address guarantor,
        int256 change
    ) external;

    function banGuarantor(
        address guarantor,
        uint256 duration,
        string calldata reason
    ) external;

    function getGuarantorProfile(address guarantor) external view returns (GuarantorProfile memory);

    function getCommitment(
        uint256 escrowId,
        address guarantor
    ) external view returns (GuarantorCommitment memory);

    function isEligibleGuarantor(
        address guarantor,
        GuarantorTier tier,
        uint256 requiredStake
    ) external view returns (bool);

    function getEscrowGuarantors(uint256 escrowId) external view returns (address[] memory);
}

contracts/interfaces/IInsurancePool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

interface IInsurancePool {
    struct PoolStats {
        uint256 totalCollected;
        uint256 totalPaidOut;
        uint256 currentBalance;
        uint256 reserveRatio;
        uint256 activeClaims;
    }

    event PremiumCollected(uint256 indexed escrowId, uint256 amount, address indexed payer);
    event ClaimFiled(uint256 indexed escrowId, address indexed claimant, uint256 requestedAmount);
    event ClaimApproved(uint256 indexed escrowId, address indexed claimant, uint256 approvedAmount);
    event ClaimRejected(uint256 indexed escrowId, address indexed claimant, string reason);
    event PayoutProcessed(uint256 indexed escrowId, address indexed recipient, uint256 amount);

    error InsufficientPoolBalance(uint256 requested, uint256 available);
    error ClaimAlreadyExists();
    error ClaimNotFound();
    error UnauthorizedClaim();
    error InvalidClaimAmount();

    function collectPremium(uint256 escrowId) external payable;

    function fileClaim(
        uint256 escrowId,
        uint256 requestedAmount,
        bytes calldata evidence
    ) external;

    function processClaim(
        uint256 escrowId,
        bool approved,
        uint256 payoutAmount
    ) external;

    function getPoolStats() external view returns (PoolStats memory);

    function calculatePremium(
        uint256 loanAmount,
        uint256 duration,
        uint256 riskScore
    ) external view returns (uint256);

    function getAvailableLiquidity() external view returns (uint256);
}

contracts/interfaces/ILenderIncentives.s.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

interface ILenderIncentives {
    struct LenderProfile {
        address lenderAddress;
        uint256 totalLent;
        uint256 totalRepaid;
        uint256 activeLoanCount;
        uint256 successfulLoanCount;
        uint256 defaultedLoanCount;
        uint256 rewardsEarned;
        uint256 joinedAt;
    }

    struct LoanOffer {
        uint256 escrowId;
        address lender;
        uint256 offeredAmount;
        uint256 interestRateBasisPoints;
        uint256 offerTimestamp;
        bool isAccepted;
        bool isActive;
    }

    struct InterestCalculation {
        uint256 baseRate;
        uint256 riskPremium;
        uint256 earlyBirdBonus;
        uint256 reputationDiscount;
        uint256 finalRate;
    }

    event LenderRegistered(address indexed lender, uint256 timestamp);
    event LoanOfferPlaced(
        uint256 indexed escrowId,
        address indexed lender,
        uint256 amount,
        uint256 interestRate
    );
    event LoanOfferAccepted(uint256 indexed escrowId, address indexed lender, uint256 amount);
    event InterestPaid(uint256 indexed escrowId, address indexed lender, uint256 amount);
    event LenderRewarded(address indexed lender, uint256 rewardAmount, string reason);
    event EarlyBirdBonusAwarded(address indexed lender, uint256 bonusAmount);

    error LenderNotRegistered();
    error OfferAlreadyExists();
    error OfferNotFound();
    error InvalidInterestRate();
    error InvalidAmount();
    error InsufficientFunds();

    function registerLender() external;

    function placeLoanOffer(
        uint256 escrowId,
        uint256 interestRateBasisPoints
    ) external payable;

    function acceptLoanOffer(
        uint256 escrowId,
        address lender
    ) external;

    function calculateInterestRate(
        uint256 escrowId,
        uint256 borrowerReputation,
        uint256 guarantorCount,
        uint256 guarantorQuality
    ) external view returns (InterestCalculation memory);

    function payInterest(
        uint256 escrowId,
        address lender,
        uint256 amount
    ) external;

    function rewardLender(
        address lender,
        uint256 amount,
        string calldata reason
    ) external;

    function getLenderProfile(address lender) external view returns (LenderProfile memory);

    function getLoanOffer(uint256 escrowId, address lender) external view returns (LoanOffer memory);

    function getEscrowOffers(uint256 escrowId) external view returns (address[] memory);
}

contracts/interfaces/IReputationSystem.s.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

interface IReputationSystem {
    struct ReputationConfig {
        uint256 successBonus;
        uint256 failurePenalty;
        uint256 collusionPenalty;
        uint256 whistleblowerBonus;
        uint256 minReputationForPrimary;
        uint256 minReputationForSecondary;
        uint256 reputationDecayRate;
        uint256 recoveryPeriod;
    }

    event ReputationScoreChanged(
        address indexed account,
        int256 change,
        uint256 newScore,
        string reason
    );

    function getReputationScore(address account) external view returns (uint256);

    function increaseReputation(
        address account,
        uint256 amount,
        string calldata reason
    ) external;

    function decreaseReputation(
        address account,
        uint256 amount,
        string calldata reason
    ) external;

    function meetsMinimumReputation(
        address account,
        uint256 minimumRequired
    ) external view returns (bool);

    function getReputationConfig() external view returns (ReputationConfig memory);
}

contracts/LenderIncentives.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./interfaces/ILenderIncentives.sol";
import "./interfaces/IGuarantorRegistry.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract LenderIncentives is ILenderIncentives, ReentrancyGuard, Ownable {
    uint256 public constant BASE_RATE_BPS = 500;
    uint256 public constant MAX_RISK_PREMIUM_BPS = 1500;
    uint256 public constant EARLY_BIRD_BONUS_BPS = 50;
    uint256 public constant MAX_REPUTATION_DISCOUNT_BPS = 300;
    uint256 public constant BASIS_POINTS = 10000;

    uint256 public constant EARLY_BIRD_THRESHOLD = 5;
    uint256 public constant MIN_GUARANTOR_COUNT = 3;
    uint256 public constant OPTIMAL_GUARANTOR_COUNT = 5;

    mapping(address => LenderProfile) private lenderProfiles;
    mapping(uint256 => mapping(address => LoanOffer)) private loanOffers;
    mapping(uint256 => address[]) private escrowLendersList;

    address public athenyxCore;
    address public guarantorRegistry;

    uint256 public totalLendersCount;

    modifier onlyCore() {
        if (msg.sender != athenyxCore) revert Unauthorized();
        _;
    }

    error Unauthorized();
    error AlreadyRegistered();
    error InvalidAddress();

    constructor() Ownable(msg.sender) {}

    function setAthenyxCore(address coreAddress) external onlyOwner {
        if (coreAddress == address(0)) revert InvalidAddress();
        athenyxCore = coreAddress;
    }

    function setGuarantorRegistry(address registryAddress) external onlyOwner {
        if (registryAddress == address(0)) revert InvalidAddress();
        guarantorRegistry = registryAddress;
    }

    function registerLender() external {
        LenderProfile storage profile = lenderProfiles[msg.sender];

        if (profile.joinedAt != 0) revert AlreadyRegistered();

        profile.lenderAddress = msg.sender;
        profile.totalLent = 0;
        profile.totalRepaid = 0;
        profile.activeLoanCount = 0;
        profile.successfulLoanCount = 0;
        profile.defaultedLoanCount = 0;
        profile.rewardsEarned = 0;
        profile.joinedAt = block.timestamp;

        totalLendersCount++;

        emit LenderRegistered(msg.sender, block.timestamp);
    }

    function placeLoanOffer(
        uint256 escrowId,
        uint256 interestRateBasisPoints
    ) external payable {
        LenderProfile storage profile = lenderProfiles[msg.sender];

        if (profile.joinedAt == 0) revert LenderNotRegistered();
        if (msg.value == 0) revert InvalidAmount();
        if (interestRateBasisPoints == 0 || interestRateBasisPoints > 5000) {
            revert InvalidInterestRate();
        }
        if (loanOffers[escrowId][msg.sender].offerTimestamp != 0) {
            revert OfferAlreadyExists();
        }

        LoanOffer storage offer = loanOffers[escrowId][msg.sender];
        offer.escrowId = escrowId;
        offer.lender = msg.sender;
        offer.offeredAmount = msg.value;
        offer.interestRateBasisPoints = interestRateBasisPoints;
        offer.offerTimestamp = block.timestamp;
        offer.isAccepted = false;
        offer.isActive = true;

        escrowLendersList[escrowId].push(msg.sender);

        uint256 offerCount = escrowLendersList[escrowId].length;
        if (offerCount <= EARLY_BIRD_THRESHOLD) {
            uint256 bonus = (msg.value * EARLY_BIRD_BONUS_BPS) / BASIS_POINTS;
            profile.rewardsEarned += bonus;
            emit EarlyBirdBonusAwarded(msg.sender, bonus);
        }

        emit LoanOfferPlaced(escrowId, msg.sender, msg.value, interestRateBasisPoints);
    }

    function acceptLoanOffer(
        uint256 escrowId,
        address lender
    ) external onlyCore nonReentrant {
        LoanOffer storage offer = loanOffers[escrowId][lender];

        if (offer.offerTimestamp == 0) revert OfferNotFound();
        if (!offer.isActive) revert OfferNotFound();

        offer.isAccepted = true;
        offer.isActive = false;

        LenderProfile storage profile = lenderProfiles[lender];
        profile.totalLent += offer.offeredAmount;
        profile.activeLoanCount++;

        (bool success, ) = athenyxCore.call{value: offer.offeredAmount}("");
        if (!success) revert TransferFailed();

        emit LoanOfferAccepted(escrowId, lender, offer.offeredAmount);
    }

    function calculateInterestRate(
        uint256 escrowId,
        uint256 borrowerReputation,
        uint256 guarantorCount,
        uint256 guarantorQuality
    ) external view returns (InterestCalculation memory) {
        InterestCalculation memory calc;

        calc.baseRate = BASE_RATE_BPS;

        uint256 riskScore = 100;

        if (borrowerReputation < 100) {
            riskScore += 50;
        } else if (borrowerReputation < 200) {
            riskScore += 25;
        } else if (borrowerReputation >= 300) {
            riskScore -= 20;
        }

        if (guarantorCount < MIN_GUARANTOR_COUNT) {
            riskScore += 30;
        } else if (guarantorCount >= OPTIMAL_GUARANTOR_COUNT) {
            riskScore -= 15;
        }

        if (guarantorQuality < 150) {
            riskScore += 20;
        } else if (guarantorQuality >= 250) {
            riskScore -= 10;
        }

        calc.riskPremium = (MAX_RISK_PREMIUM_BPS * riskScore) / 100;
        if (calc.riskPremium > MAX_RISK_PREMIUM_BPS) {
            calc.riskPremium = MAX_RISK_PREMIUM_BPS;
        }

        uint256 offerCount = escrowLendersList[escrowId].length;
        if (offerCount < EARLY_BIRD_THRESHOLD) {
            calc.earlyBirdBonus = EARLY_BIRD_BONUS_BPS;
        }

        if (borrowerReputation >= 300) {
            calc.reputationDiscount = MAX_REPUTATION_DISCOUNT_BPS;
        } else if (borrowerReputation >= 200) {
            calc.reputationDiscount = MAX_REPUTATION_DISCOUNT_BPS / 2;
        }

        calc.finalRate = calc.baseRate + calc.riskPremium + calc.earlyBirdBonus;
        if (calc.finalRate > calc.reputationDiscount) {
            calc.finalRate -= calc.reputationDiscount;
        }

        return calc;
    }

    function payInterest(
        uint256 escrowId,
        address lender,
        uint256 amount
    ) external onlyCore nonReentrant {
        if (amount == 0) revert InvalidAmount();

        LenderProfile storage profile = lenderProfiles[lender];
        profile.totalRepaid += amount;

        (bool success, ) = lender.call{value: amount}("");
        if (!success) revert TransferFailed();

        emit InterestPaid(escrowId, lender, amount);
    }

    function rewardLender(
        address lender,
        uint256 amount,
        string calldata reason
    ) external onlyCore nonReentrant {
        if (amount == 0) revert InvalidAmount();

        LenderProfile storage profile = lenderProfiles[lender];
        profile.rewardsEarned += amount;

        (bool success, ) = lender.call{value: amount}("");
        if (!success) revert TransferFailed();

        emit LenderRewarded(lender, amount, reason);
    }

    function markLoanComplete(address lender, bool successful) external onlyCore {
        LenderProfile storage profile = lenderProfiles[lender];

        profile.activeLoanCount--;

        if (successful) {
            profile.successfulLoanCount++;
        } else {
            profile.defaultedLoanCount++;
        }
    }

    function getLenderProfile(address lender) external view returns (LenderProfile memory) {
        return lenderProfiles[lender];
    }

    function getLoanOffer(uint256 escrowId, address lender) external view returns (LoanOffer memory) {
        return loanOffers[escrowId][lender];
    }

    function getEscrowOffers(uint256 escrowId) external view returns (address[] memory) {
        return escrowLendersList[escrowId];
    }

    function getBestOffer(uint256 escrowId) external view returns (address bestLender, uint256 lowestRate) {
        address[] memory lenders = escrowLendersList[escrowId];

        if (lenders.length == 0) return (address(0), 0);

        bestLender = lenders[0];
        lowestRate = loanOffers[escrowId][lenders[0]].interestRateBasisPoints;

        for (uint256 i = 1; i < lenders.length; i++) {
            uint256 rate = loanOffers[escrowId][lenders[i]].interestRateBasisPoints;
            if (rate < lowestRate && loanOffers[escrowId][lenders[i]].isActive) {
                lowestRate = rate;
                bestLender = lenders[i];
            }
        }
    }

    error TransferFailed();

    receive() external payable {}
}

